package com.telusko.joblisting.model;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.Tag;
import static org.junit.jupiter.api.Assertions.*;

class PostGetDescTest {

    @Test
    @Tag("valid")
    public void testGetDescWithValidValue() {
        Post post = new Post();
        String expectedDescription = "Software Engineer";
        post.setDesc(expectedDescription);
        assertEquals(expectedDescription, post.getDesc(), "The description should match the set value.");
    }

    @Test
    @Tag("invalid")
    public void testGetDescWithNullValue() {
        Post post = new Post();
        post.setDesc(null);
        assertNull(post.getDesc(), "The description should be null.");
    }

    @Test
    @Tag("boundary")
    public void testGetDescWithEmptyString() {
        Post post = new Post();
        String expectedDescription = "";
        post.setDesc(expectedDescription);
        assertEquals(expectedDescription, post.getDesc(), "The description should be an empty string.");
    }

    @Test
    @Tag("boundary")
    public void testGetDescWithLongString() {
        Post post = new Post();
        String expectedDescription = "A".repeat(1000); // 1000 characters long
        post.setDesc(expectedDescription);
        assertEquals(expectedDescription, post.getDesc(), "The description should match the long string value.");
    }
}
